$('.menu-toggle').click(function(e){
    e.preventDefault();
    if($('body').hasClass('open')){ $('body').removeClass('open'); }else{ $('body').addClass('open');  }
});




$('form.looking').submit(function(e){

    e.preventDefault();

    var old_text = $('form.looking button[type=submit]').html();
    $('form.looking button[type=submit]').html(' <i class="fas fa-circle-notch fa-spin"></i>');

    $.post('_ajax/_ajaxSearch.php', $('form.looking').serialize(), function(result){
        

         $('form.looking').parent().find('.result').remove();
         $('form.looking').parent().append(result);

        $('form.looking button[type=submit]').html(old_text);

    });








});


                 
               
                        $('.collapse .title').click(function(){
                                
                               

                                var item = $(this).parent();
                                if($(item).hasClass('open')){

                                        $(item).removeClass('open');

                                }else{
                                        $('.table-whois .collapse').removeClass('open');
                                        $(item).addClass('open');

                                }


                        });

                        $('.collapse .action span').click(function(){
                            
                                
                                
                                $(this).parent().parent().addClass('overflow');
                                $(this).parent().remove();


                        });

                
